import React, { useState, useEffect } from "react";
import { Table, Input } from "antd";
import "antd/dist/antd.css"; // Import Ant Design CSS
import "./UserList.css"; // Import your custom CSS file (for Tailwind CSS styles)

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [searchText, setSearchText] = useState("");

  useEffect(() => {
    // Fetch the list of users from your API database
    // Replace 'YOUR_API_ENDPOINT' with your actual API endpoint
    fetch("http://localhost:3000/api/v1/getAllUsers")
      .then((response) => response.json())
      .then((data) => setUsers(data))
      .catch((error) => console.error("Error fetching users:", error));
  }, []);

  // Define the columns for the table
  const columns = [
    {
      title: "ID",
      dataIndex: "id",
      key: "id",
    },
    {
      title: "Name",
      dataIndex: "name",
      key: "name",
    },
    {
      title: "Email",
      dataIndex: "email",
      key: "email",
    },
    {
      title: "City",
      dataIndex: "city",
      key: "city",
    },
    {
      title: "Phone",
      dataIndex: "phone",
      key: "phone",
    },
  ];

  // Filter the users based on the search text
  const filteredUsers = users.filter((user) =>
    user.name.toLowerCase().includes(searchText.toLowerCase())
  );

  return (
    <div className="p-4">
      {/* Search input */}
      <Input
        placeholder="Search by name"
        value={searchText}
        onChange={(e) => setSearchText(e.target.value)}
        className="mb-4"
      />

      {/* User table */}
      <Table dataSource={filteredUsers} columns={columns} />
    </div>
  );
};

export default UserList;
